import React, { Component } from 'react'
import Router from 'next/router'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'

const CREATE_PRODUCTD_MUTATION = gql`
    mutation CREATE_PRODUCTD_MUTATION(
        $name: String!,
        $image: String!,
        $content: String!,
        $saleprice: Int,
        $price: Int!
    ){
        createProduct(
            title: $name,
            image: $image,
            content: $content,
            saleprice: $saleprice,
            price: $price

            ){
            id
        }
    }
`;


class CreateProductd extends Component {
    state = {
        name: '',
        image: '',
        content: '',
        saleprice: '',
        price: '',
    };
    handleChange = e => {
        const {name, type, value} = e.target;
        this.setState({ [name]: e.target.value });
    };
    render() {
        return (
            <div>
                <h2>Add New Product</h2>
                <Mutation mutation={CREATE_PRODUCTD_MUTATION} variables={this.state}>
                    {(createProductd, {loading, error}) => (
                       
                <form 
                action=""
                onSubmit={async e => {
                    e.preventDefault();

                    const res = await createProductd();
                    console.log(res);

                    Router.push({
                        pathname: '/admin/Products'
                    });
                    
                }}
                >
                    <fieldset disabled={loading}>
                        <p>
                        <label htmlFor="name">Title</label>
                        <input
                        id="name"
                        name="name" 
                        value= {this.state.name} 
                        placeholder="post title" 
                        type="text"
                        onChange = {this.handleChange}
                        />
                        </p>
                        <p>
                        <label htmlFor="image">Image</label>
                        <input
                        id="image"
                        name="image" 
                        value= {this.state.image} 
                        placeholder="post image" 
                        type="text"
                        onChange = {this.handleChange}
                        />
                        </p>
                        <p>
                        <label htmlFor="image">Price</label>
                        <input
                        id="image"
                        name="image" 
                        value= {this.state.image} 
                        placeholder="post image" 
                        type="text"
                        onChange = {this.handleChange}
                        />
                        </p>
                        <p>
                        <label htmlFor="saleprice">Saleprice</label>
                        <input
                        id="saleprice"
                        name="saleprice" 
                        value= {this.state.saleprice} 
                        placeholder="Ente" 
                        type="text"
                        onChange = {this.handleChange}
                        />
                        </p>
                        <p>
                        <label htmlFor="content">Content</label>

                        <textarea
                        name="content" 
                        id="content" 
                        cols="30" 
                        rows="10"
                        value= {this.state.content} 
                        placeholder="Type post content"
                        onChange = {this.handleChange}
                        />
                        </p>
                        <button type="submit">Submit</button>
                    </fieldset>
                </form> 
                )}
                </Mutation>               
            </div>
        )
    }
}
export default CreateProductd;
